/*---------------Eligible needs----------------------*/

var eligibleText = "",
	nonEligibleText = "",
	nonAssessedText = "",
	domainName=[
"Maintaining Personal Hygiene",
"Being Appropriately Clothed",
"Managing Toilet Needs",
"Being able to make use of the Home Safely",
"Managing and Maintaining Nutrition",
"Maintaining a Habitable Home Environment",
"Making use of Necessary Facilities in the Local Community",
"Developing and maintaining family or other personal relationships",
"Accessing and Engaging in Work, Training, Education or Volunteering",
"Carrying out any Caring Responsibilities the Adult has for a Child"],
	flagEligible=0,
	flagNE=0,
	flagNC=0;

var phyMental=form.getCurrentScore("aspFLDNeedsDueToMentalImp");

for(i=1;i<=10;i++){
	var achieveScore = form.getCurrentScore("aspFLDAblAchieve"+i),
		impactScore = form.getCurrentScore("aspFLDSigImpact"+i);

	if((achieveScore >= 2) && (achieveScore <= 5) && (impactScore == 1) && (phyMental == 1)){
		if(flagEligible == 0){
			eligibleText += domainName[i-1];
			form.setAnswerText(("aspFLDEligible" + i), "Y");
			flagEligible++;
		}else{
			eligibleText += "\n" + domainName[i-1];
			form.setAnswerText(("aspFLDEligible" + i), "Y");
			flagEligible++;
		}
	}else if((achieveScore) && (impactScore)){
		if(flagNE == 0){
			nonEligibleText += domainName[i-1];
			form.setAnswerText(("aspFLDEligible" + i), "N");
			flagNE++;
		}else{
			nonEligibleText += "\n" + domainName[i-1];
			form.setAnswerText(("aspFLDEligible" + i), "N");
			flagNE++
		}
	}else{
		if(flagNC == 0){
			nonAssessedText += domainName[i-1];
			form.setAnswerText(("aspFLDEligible" + i), "N");
			flagNC++;
		}else{
			nonAssessedText += "\n" + domainName[i-1];
			form.setAnswerText(("aspFLDEligible" + i), "N");
			flagNC++
		}

	}
}

form.setAnswerText("aspFLDEligible", eligibleText);
form.setAnswerText("aspFLDNotEligible", nonEligibleText);
form.setAnswerText("aspFLDNotConsidered", nonAssessedText);

if(flagEligible >= 2){
	form.setAnswerText("aspFLDOverallEligDec","Eligible");
}else{
	form.setAnswerText("aspFLDOverallEligDec","Not Eligible");
}

